INSERT INTO `tbkuesioner` VALUES ('1', 'Berapa persen kira-kira dana pengembangan TIK yang dialokasikan oleh kampus dari pengeluaran yang ada ?', '1', 'komitmen pimpinan', 'Dibawah 5%', 'Antara 5%-10%', 'Antara 11%-20%', 'Antara 21%-30%', 'Diatas 30%');
INSERT INTO `tbkuesioner` VALUES ('2', 'Apakah perguruan tinggi memiliki program sosialisasi dan edukasi mengenai pentingnya pemanfaatan TIK kampus?', '1', 'komitmen pimpinan', 'Ada, namun bersifat sporadis dan tidak terencana', 'Ada, dengan ruang lingkup dan target sasaran audience sesuai dengan anggaran', 'Ada, terencana dengan baik dilaksanakan secara konsisten, kontinyu dan berkesinambungan', 'Ada, terencana dengan baik dan dilaksanakan secara konsisten, kontinyu dan berkesinambungan dan mendapatkan banyak bantuan serta didukung dari stakeholder lain diluar perangkat organisasi perguruan', 'Ada, terencana dengan baik dan dilaksanakan secara konsisten, kontinyu dan berkesinambungan dan mendapatkan banyak bantuan serta didukung dari stakeholder lain diluar perangkat organisasi perguruan ti');
INSERT INTO `tbkuesioner` VALUES ('3', 'Apakah perguruan tinggi menggunakan media tertentu dalam melakukan sosialisasi dan edukasi mengenai pentingnya pemanfaatan teknologi informasi dan komunikasi kampus ?', '1', 'komitmen pimpinan', 'Tidak', 'Ya, di beberapa media namun dalam ukuran kecil', 'Ya, di sejumlah media dan cukup efektif', 'Ya, di sejumlah media dan sangat signifikan dampaknya', 'Ya, cukup banyak jalur media yang memberikan kontribusi positif dan signifikan terhadap perkembangan TIK perguruan tinggi.');
INSERT INTO `tbkuesioner` VALUES ('4', 'Dari manasajakah sumber dana terbesar yang diperuntukkan untuk melakukan investasi dalam membangun TIK Perguruan tinggi ?', '1', 'alokasi sumber daya', 'Tidak dari mana-mana, karena memang belum memiliki TIK', 'Diambil melalui dana yang berasal dari uang bayaran mahasiswa, baik yang berupa sumbangan maupun uang kuliah', 'Dialokasikan khusus oleh pemilik perguruan tinggi dalam bentuk belanja modal', 'Sebagian dari dana bantuan pemerintah Indonesia dan sisa hasil usaha penyelenggaraan belajar mengajar', 'Investasi pihak ketiga (eksternal) yang memiliki komitme penuh jangka panjang dalam membangun perguruan tinggi yang ada.');
INSERT INTO `tbkuesioner` VALUES ('5', 'Berapa kira-kira nilai investasi TIK dalam tiga tahun terakhir ?', '1', 'alokasi sumber daya', 'Kurang dari Rp. 100 juta', 'Rp. 100 â€“ 500 juta', 'Rp. 500 juta â€“ 1 Milyar', 'Rp. 1-3 Milyar', '> Rp. 3 Milyar');
INSERT INTO `tbkuesioner` VALUES ('6', 'Berapa jumlah karyawan yang berlatar belakang pendidikan perguruan tinggi bidang komputer atau informatika ?', '1', 'alokasi sumber daya', 'Kurang dari 1% dari keseluruhan pegawai di Perguruan tinggi', '2%-5% dari keseluruhan pegawai di Perguruan tinggi', '6%-15% dari keseluruhan pegawai di Perguruan tinggi', '16%-35% dari keseluruhan pegawai di Perguruan tinggi', '>35% dari keseluruhan pegawai di Perguruan tinggi');
INSERT INTO `tbkuesioner` VALUES ('7', 'Apakah perguruan tinggi memiliki unit unit atau divisi khusus yang bertanggung jawab terhadap perencanaan, pembangunan, penerapan, pengendalian  dan pengembangan TIK di lingkungan kampus ?', '1', 'unit pengelola teknologi', 'Tidak ada', 'Ada, namun tidak beroperasi secara optimal karena masih mengerjakan program atau proyek', 'Ada, dan merupakan suatu unit organisasi tersendiri dengan tanggung jawab yang jelas', 'Ada, merupakan suatu unit organisasi tersendiri, yang berjalan berdasarkan tanggung jawab yang jelas sesuai dengan SOP yang telah disusun dan dikembangkan', 'Ada, merupakan sebuah unit organisasi independen, dimana didukung oleh berbagai pihak ketiga yang sesuai dengan bidang keahliannya, dibawah tata kelola manajemen yang rapi dan transparan.');
INSERT INTO `tbkuesioner` VALUES ('8', 'Jika perguruan tinggi memiliki unit khusus yang dibentuk oleh pihak kampus untuk mengelola TIK yang dimiliki, dibawah komando siapa unit khusus ini berada ?', '1', 'unit pengelola teknologi', 'Tidak dibawah siapa-siapa, karena tidak terdapat unit yang dimaksud', 'Berada dibawah komando salah satu unit kegiatan yang ada di perguruan tinggi', 'Berada dibawah komando salah satu divisi kegiatan yang ada di perguruan tinggi', 'Berada dibawah komando salah satu wakil pimpinan perguruan tinggi institusi', 'Langsung dibawah komando pimpinan perguruan tinggi institusi');
INSERT INTO `tbkuesioner` VALUES ('9', 'Apakah perguruan tinggi memiliki unit atau divisi yang bertanggung jawab terhadap pelatihan TIK dalam lingkungan kampus ?', '1', 'unit pengelola teknologi', 'Tidak ada', 'Ada, namun tidak berorientasi secara optimal karena masih mengerjakan hal-hal yang bersifat ad-hoc, atau berbasis program atau proyek', 'Ada, dan merupakan suatu unit organisasi tersendiri dengan tanggung jawab yang jelas', 'Ada, merupakan suatu unit organisasi tersendiri, yang berjalan berdasarkan tanggung jawab yang jelas sesuai dengan SOP yang telah disusun dan dikembangkan.', 'Ada, merupakan sebuah unit organisasi independen, dimana didukung oleh berbagai pihak ketiga yang sesuai dengan bidang keahliannya, di bawah tata kelola manajemen yang rapi dan transparan');
INSERT INTO `tbkuesioner` VALUES ('10', 'Apakah perguruan tinggi memiliki kebijakan dan peraturan yang mengharuskan setiap civitas akademika memanfaatkan secara intensif TIK dalam lingkungan kampus sebagai penunjang kegiatan belajar mengajar', '1', 'kebijakan dan sistem insentif', 'Tidak memiliki', 'Tidak memiliki secara tertulis, namun sering disampaikan dan diwacanakan oleh segenap pimpinan', 'Terdapat kebijakan tertulis melalui surat keputusan pimpinan perguruan tinggi mengenai keharusan menggunakan TIK dalam lingkungan kampus.', 'Terdapat sejumlah kebijakan terkait dengan pemanfaatan TIK sesuai tata aturan yang ada, dan dijadikan sebagai salah satu pengukur kinerja unit maupun individu terkait', 'Terdapat berbagai kebijakan dan peraturan keharusan pemanfaatan TIK dalam lingkungan kampus dipergunakan sebagai komponen pengukur kinerja dan terdapat model insentif yang diterapkan manajemen kampus ');
INSERT INTO `tbkuesioner` VALUES ('11', 'Apakah ada sistem insentif yang dikembangkan oleh perguruan tinggi untuk mendorong agar segenap civitas akademika di kampus memanfaatkan teknologi informasi dan komunikasi seoptimal mungkin ?', '1', 'kebijakan dan sistem insentif', 'Tidak ada', 'Ada, namun tidak jelas dan konsisten penerapannya', 'Ada, dimana implementasinya diatur dalam peraturan perguruan tinggi', 'Ada, dimana implementasinya telah terintegrasi dengan sistem pengelolaan sumber daya manusia yang dimiliki oleh perguruan tinggi', 'Ada, dimana model dan implementasinya selain telah terintegrasi secara baik dengan sistem sumber daya manusia, tingkat efekivitasnya pun diukur dan dinilai dari hari ke hari.');
INSERT INTO `tbkuesioner` VALUES ('12', 'Bagaimana karyawan dan staf perguruan tinggi belajar mengenai cara memanfaatkan teknologi informasi dan komunikasi (missal : menggunakan computer dan internet) ?', '1', 'kebijakan dan sistem insentif', 'Kebanyakan belajar sendiri (mandiri) atau otodidak', 'Kebanyakan belajar dari orang lain dan sumber referensi (buku)', 'Kebanyakan belajar dari orang lain, sumber referensi dan mengikuti pendidikan non formal (pelatihan dan kursus)', 'Kebanyakan belajar dari orang lain, sumber referensi dan mengikuti pendidikan formal dan vokasi', 'Kebanyakan belajar dari orang lain, sumber referensi, mengikuti pendidikan formal (akademik maupun vokasi) maupun informal.');
INSERT INTO `tbkuesioner` VALUES ('13', 'Bagaimana karyawan dan staf perguruan tinggi belajar mengenai cara memanfaatkan teknologi informasi dan komunikasi (misal : menggunakan komputer dan internet) ?', '1', 'kebijakan dan sistem insentif', 'Kebanyakan belajar sendiri (mandiri) atau otodidak', 'Kebanyakan belajar dari orang lain dan sumber referensi (buku)', 'Kebanyakan belajar dari orang lain, sumber referensi dan mengikuti pendidikan non formal (pelatihan dan kursus)', 'Kebanyakan belajar dari orang lain, sumber referensi dan mengikuti pendidikan formal dan vokasi', 'Kebanyakan belajar dari orang lain, sumber referensi, mengikuti pendidikan formal (akademik maupun vokasi) maupun informal');
INSERT INTO `tbkuesioner` VALUES ('14', 'Apakah perguruan tinggi telah memiliki dan menyusun Rencana Strategis Pengembangan TIK (IT Master Plan) untuk kebutuhan kampus ?', '1', 'Renstra dan peta jalan', 'Tidak memiliki', 'Memiliki, namun sudah lama tidak dimutakhirkan (update)', 'Memiliki dan setiap tahun diperbaharui', 'Memiliki dan secara konsisten seluruh program pengembangan TIK kampus didasari pada dokumen ini.', 'Memiliki dan secara konsisten seluruh program pengembangan TIK kampus didasari pada dokumen ini dan dilakukan audit terhadap efektivitas kepatuhannya.');
INSERT INTO `tbkuesioner` VALUES ('15', 'Apakah Rencana Strategis Pengembangan TIK (IT Master Plan) yang dimiliki dipergunakan sebagai panduan / acuan dalam menyelenggarakan berbagai proyek kegiatan teknologi informasi dan komunikasi perguru', '1', 'Renstra dan peta jalan', 'Tidak karena memang belum memiliki Rencana Strategis Pengembangan IT (IT Master Plan)', 'Ya, namun baru sebagian kecil saja proyek atau kegiatan yang mengacu pada dokumen ini.', 'Ya, rata-rata proyek atau kegiatan mengacu pada dokumen ini', 'Ya, sebagian besar proyek atau kegiatan mengacu pada dokumen ini.', 'Ya, semua proyek mengacu pada dokumen ini, jika ada proyek yang tidak termasuk maka menjadi bahan masukan untuk merevisi dokumen yang ada.');
INSERT INTO `tbkuesioner` VALUES ('16', 'Bagaimana cara perguruan tinggi menyusun kebutuhan teknologi informasi dan komunikasi yang diinginkan ? ', '1', 'Perencanaan dan pengorganisasian', 'Berkaca pada perguruan tinggi lain, dan mencoba mengikutinya.', 'Menyusun kebutuhan TIK berdasarkan keinginan orang yang menangani TIK', 'Menyusun kebutuhan TIK berdasarkan jumlah unit-unit yang membutuhkan TIK', 'Menyusun kebutuhan TIK berdasarkan analisis kebutuhan masing-masing unit', 'Menyusun kebutuhan TIK berdasarkan analisis kebutuhan masing-masing unit dan senantiasa diperbaharui dan dimutakhirkan sesuai perkembangan organisasi.');
INSERT INTO `tbkuesioner` VALUES ('17', 'Apakah perguruan tinggi memiliki dokumen arsitektur yang dipergunakan sebagai panduan atau acuan teknis pembangunan teknologi informasi dan komunikasi di perguruan tinggi ?', '1', 'Perencanaan dan pengorganisasian', 'Tidak memiliki', 'Ya, namun sebagian kecil inisiatif pembangunan teknologi informasi dan komunikasi yang mengadu pada rancangan arsitektur ini', 'Ya, rata-rata inisiatif pembangunan teknologi informasi dan komunikasi mengacu pada rancangan arsitektur ini.', 'Ya, sebagian besar inisiatif pembangunan teknologi informasi dan komunikasi mengacu pada rancangan arsitektur ini', 'Ya, semua inisiatif pembangunan teknologi informasi dan komunikasi mengacu pada rancangan arsitektur ini.');
INSERT INTO `tbkuesioner` VALUES ('18', 'Apakah perguruan tinggi anda memiliki standar terkait teknologi informasi dan komunikasi di perguruan tinggi yang akan diadopsi (missal : standar dalam tipe aplikasi, fitur / spesifikasi piranti keras', '1', 'Perencanaan dan pengorganisasian', 'Tidak ada sama sekali', 'Ada, kadang-kadang dipergunakan, kadang-kadang tidak', 'Ada, dipergunakan secara konsisten sebagai basis pengelolaan dan dikembangkan secara kontinyu dan berkesinambungan', 'Ada, dipergunakan secraa konsisten sebagai basis pengelolaan dan dikembangkan secara kontinyu dan berkesinambungan', 'Ada, dipergunakan secara konsisten sebagai basis pengelolaan, dikembangkan secara kontinyu dan berkesinambungan, dan dievaluasi / diaudit kepatuhan adopsinya');
INSERT INTO `tbkuesioner` VALUES ('19', 'Apakah memiliki mekanisme penghitungan cost benefit dari setiap program, atau inisiatif investasi pengembangan teknologi informasi dan komunikasi di perguruan tinggi ?', '1', 'Perencanaan dan pengorganisasian', 'Tidak ada, namun sudah dipikirkan untuk menyusunnya', 'Ada, namun belum secara konsisten diadopsi sepenuhnya', 'Ada, dan dijadikan sebagai acuan baku pelaksanaan manajemen proyek teknologi informasi', 'Ada, dipergunakan sebagai acuan baku pelaksanaan manajemen proyek teknologi informasi dan dijadikan basis evaluasi kinerja perangkat organisasi perguruan tinggi', 'Ada, dipergunakan sebagai acuan baku pelaksanaan manajemen proyek teknologi informasi dan dijadikan basis evaluasi kinerja perguruan tinggi dan senantiasa dikembangkan / direvisi mekanisme baku yang d');
INSERT INTO `tbkuesioner` VALUES ('20', 'Apakah segenap pimpinan perguruan tinggi secara intensif menggunakan beragam aplikasi teknologi informasi dan komunikasi dalam aktivitas sehari-harinya ?', '1', 'Pengadaan dan penerapan ', 'Tidak sama sekali', 'Ya, menggunakan namun tidak optimal dan tidak intensif', 'Ya, menggunakan secara efektif dan cukup intensif', 'Ya. menggunakan secara sangat intensif, efektif dan optimal', 'Ya, menggunakan secara sangat intensif, efektif, optimal dan telah berhasil menularkan kebiasaan ini kepada segenap pimpinan manajerial di bawahnya');
INSERT INTO `tbkuesioner` VALUES ('21', 'Apakah suasana atau budaya memanfaatkan teknologi informasi dan komunikasi terasa di dalam lingkungan kampus ?', '1', 'Pengadaan dan penerapan', 'Tidak sama sekali', 'Cukup terasa namun dalam keadaan atau waktu-waktu tertentu saja', 'Cukup terasa dalam situasi sehari-hari', 'Ya, sangat terasa karena hampir seluruh lokasi strategis Nampak segenap civitas akademika memanfaatkan teknologi', 'Ya, sangat terasa karena secara massif terlihat dari aktivitas segenap civitas akademika yang berinteraksi dengan teknologi di setiap sudut-sudut kampus');
INSERT INTO `tbkuesioner` VALUES ('22', 'Apakah perguruan tinggi memiliki dokumen yang jelas dan detail (misal : standar operasional prosedur) dari proses pengelolaan teknologi informasi dan komunikasi di perguruan tinggi ?', '1', 'Pengelolaan dan pengembangan', 'Tidak ada, semua berjalan seperti kebiasaan saja', 'Tidak ada, semua berjalan seperti kebiasaan saja', 'Ada, dipergunakan sebagai panduan kerja namun tidak pernah dikaji maupun dimutakhirkan', 'Ada, dipergunakan sebagai panduan kerja senantiasa dimutakhirkan sesuai dengan perbaikan dan dipakai sebagai basis evaluasi kinerja', 'Ada, dipergunakan sebagai panduan kerja senantiasa dimutakhirkan sesuai dengan perbaikan dan dipakai sebagai basis evaluasi kinerja dan penentuan remunerasi berdasarkan beban kerja dan tanggung jawab');
INSERT INTO `tbkuesioner` VALUES ('23', 'Apakah memiliki standar baku dalam manajemen pengelolaan proyek (project management) teknologi informasi dan komunikasi di perguruan tinggi ?', '1', 'Pengelolaan dan pengembangan', 'Tidak ada, namun sudah dipikirkan untuk menyusunnya', 'Ada, namun belum secara konsisten diadopsi sepenuhnya', 'Ada, dan dijadikan acuan baku pelaksanaan manajemen proyek teknologi informasi dan dijadikan basis evaluasi kinerja perangkat organisasi perguruan tinggi', 'Ada, dipergunakan sebagai acuan baku pelaksanaan manajemen proyek teknologi informasi dan dijadikan basis kinerja perangkan organisasi perguruan tinggi', 'Ada, dipergunakan sebagai acuan baku pelaksanaan manajemen proyek teknologi informasi dan dijadikan basis kinerja perangkan organisasi perguruan tinggi dan senantiasa dikembangkan / direvisi mekanisme');
INSERT INTO `tbkuesioner` VALUES ('24', 'Apakah perguruan tinggi anda memiliki prosedur penanganan keadaan darurat (contingency plan) bila terjadi gangguan pada sistem teknologi informasi dan komunikasi ?', '1', 'Pengelolaan dan pengembangan', 'Tidak ada, semua berjalan secara naluri dan kebiasaan saja', 'Ada, dipergunakan sebagai panduan kerja namun tidak pernah dikaji dan dimutakhirkan', 'Ada, dipergunakan sebagai panduan kerja, senantiasa dimutakhirkan sesuai dengan perbaikan', 'Ada, dipergunakan sebagai panduan kerja, senantiasa dimutakhirkan sesuai dengan perbaikan, dan dipakai sebagai basis evaluasi kinerja', 'Ada, dipergunakan sebagai panduan kerja, senantiasa dimutakhirkan sesuai dengan perbaikan, dan dipakai sebagai basis evaluasi kinerja serta penentuan remunerasi berdasarkan beban kerja dan tanggung ja');
INSERT INTO `tbkuesioner` VALUES ('25', 'Apakah tersedia pelayanan “help desk” untuk membantu para pengguna teknologi informasi dan komunikasi yang memiliki masalah ?', '1', 'Pengelolaan dan pengembangan', 'Tidak tersedia', 'Tersedia, namun kinerjanya tidak seperti yang diharapkan', 'Tersedia dan beroperasi dengan baik pada jam kerja', 'Tersedia dan beroperasi dengan baik selama 24 jam sehari, kecuali hari libur atau Sabtu / Minggu', 'Tersedia dan beroperasi dengan baik selama 24 jam sehari, dan tujuh hari seminggu');
INSERT INTO `tbkuesioner` VALUES ('26', 'Apakah dilakukan proses audit terhadap efektivitas penerapan teknologi informasi dan komunikasi di lingkungan kampus ?', '1', 'Pemantauan dan penilaian', 'Tidak sama sekali', 'Pada dasarnya dilakukan evaluasi secara umum, namun tidak berdasarkan prosedur baku audit', 'Ya, dilakukan audit oleh pihak internal yang berkepentingan dan memiliki kapabilitas', 'Ya, dilakukan audit oleh pihak eksternal yang berkepentingan dan memiliki kapabilitas', 'Ya, dilakukan audit oleh pihak internal dan eksternal secara independen dan bertanggung jawab');
INSERT INTO `tbkuesioner` VALUES ('27', 'Apakah perguruan tinggi memiliki indikator kinerja utama (Key Performance Indicator) dalam implementasi teknologi informasi dan komunikasi kampus ?', '1', 'Pemantauan dan penilaian', 'Tidak ada, semuanya berjalan secara naluri dan kebiasaan', 'Ada, dipergunakan sebagai panduan kerja, namun tidak pernah dikaji maupun dimutakhirkan', 'Ada, dipergunakan sebagai panduan kerja, senantiasa dimutakhirkan sesuai dengan perbaikan', 'Ada, dipergunakan sebagai panduan kerja, senantiasa dimutakhirkan sesuai dengan perbaikan dan dipakai sebagai basis evaluasi kinerja', 'Ada, dipergunakan sebagai panduan kerja, senantiasa dimutakhirkan sesuai dengan perbaikan dan dipakai sebagai basis evaluasi kinerja dan penentuan remunerasi berdasarkan beban kerja dan tanggung jawab');
INSERT INTO `tbkuesioner` VALUES ('28', 'Berapa persen kira-kira materi perkuliahan yang telah berbentuk file elektronik atau digital ?', '2', 'Dosen dan peneliti', 'Dibawah 10%', 'Kira-kira antara 11%-25%', 'Kira-kira antara 26%-50%', 'Kira-kira antara 51%-75%', 'Kira-kira diatas 76%');
